SELECT DISTINCT t.user_name `name`, COUNT(user_id) icon, ROUND(SUM(t.backer_money), 2) `data` FROM t_modian_comment t WHERE t.project_id IN
#{item}
GROUP BY t.user_id, t.user_name ORDER BY ROUND(SUM(t.backer_money), 2) DESC

SELECT j.nickname `name`, j.avatar icon, COUNT( j.user_id ) `count`, ROUND( SUM( j.money ), 2 ) `data` FROM taoba_join j WHERE j.detail_id IN
#{item}
GROUP BY j.user_id, j.nickname, j.avatar ORDER BY ROUND( SUM( j.money ), 2 ) DESC
